	public static int[] intersection(int[] nums1, int[] nums2) {
		HashSet<Integer> hSet1 = new HashSet<Integer>();
        HashSet<Integer> hSet2 = new HashSet<Integer>();
        
        for(int i : nums1) hSet1.add(i);
        
        for(int i : nums2){
          if(hSet1.contains(i)) {
              hSet2.add(i);  
          }
        }
        
        int[] res = new int[hSet2.size()];
        int i =0;
        Iterator<Integer> itr = hSet2.iterator();
        while(itr.hasNext()){
            res[i] = (int)itr.next();
            i++;
        }
        
        return res;   
    }
